SELECT gar.sessionId__c as "Session Id", ggn.timestamp__c AS "Generation Timestamp", gar.userId__c AS "User Id", gat.tagValue__c AS "User Utterance", gar.prompt__c AS "Prompt", ggn.responseText__c AS "Response Text" FROM GenAIGeneration__dlm ggn JOIN GenAiGatewayResponse__dlm grs ON ggn.generationResponseId__c = grs.generationResponseId__c JOIN GenAIGatewayRequest__dlm gar ON grs.generationRequestId__c = gar.gatewayRequestId__c JOIN GenAIGatewayRequestTag__dlm gat ON gar.gatewayRequestId__c = gat.parent__c AND gat.tag__c = 'user_utterance' LEFT JOIN GenAIFeedback__dlm gaf ON gar.generationGroupId__c = gaf.generationGroupId__c LEFT JOIN GenAIFeedbackDetail__dlm gfd ON gaf.feedbackId__c = gfd.parent__c order by ggn.timestamp__c desc